public class if_stataments {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n = 10;
		if(n!= 100)
			System.out.println("Yes!!");
		else if(n == 10)
			System.out.println("Yes Yes!!");
		else
			System.out.println("No!!");
		
		int num = 5;
		while(num < 10)
		{
			
			System.out.println("Thank you!!"+num);
			if(num==7)
				break;
			
			num++;
		}
				
	}

}
